import React, {useEffect, useState} from "react";
import { useHistory , useLocation, useParams} from "react-router-dom";
import axios from "axios";
import './editor.css'
import { toast } from "react-toastify";

const estadoInicial ={
    nombre: "",
    correo: "",
    contacto: "",

};

const Editor =() =>{
    const [state, setState] = useState(estadoInicial);
    const{nombre, correo, contacto} = estadoInicial;

    const history = useHistory();

    const {id} = useParams();
    useEffect(() => {
        if (id) {
            getSingleUser(id);
        }
    }, [id])

    const getSingleUser = async (id) => {
        const response = await axios.delete(`http://localhost:5000/usuario/${id}`)
            if (response.status === 200) {
                setState({...response.data[0]});
            }
    }

    const AddUsuario = async(data)=>{
        const response = await axios.post("http://localhost:5000/usuario", data);
        if(response.status === 200){
            toast.success(response.data);
        }
    };
    const updateUsuario = async(data, id)=>{
        const response = await axios.put(`http://localhost:5000/usuario/${id}`, data);
        if(response.status === 200){
            toast.success(response.data);
        }
    };
    // Se agrega ventana emergente para avisar la ausencia de algún dato
    const handleSubmit = (evento)=>{
        evento.preventDefault();
        if(!nombre || !correo||!contacto){
            toast.error("Por favor agregue todos los datos");
        }else{
            if (!id) {
                AddUsuario(state);
            }else{
                updateUsuario(state, id);
            }
            
            setTimeout(() => history.push("/"), 500);
        }


    };
    const handleInputChange = (evento) =>{
        let {nombre, value} = evento.target;
        setState({...state,[nombre]:value});
    };
    return(
        <div style={{marginTop:"100px"}}>
            <form style={{
                margin:"auto", 
                padding:"15px", 
                maxWidth:"400px", 
                alignContent:"center"}}
                
                onSubmit = {handleSubmit}>
                    <label htmlFor = "nombre">Nombre</label>
                    <input 
                        type="text" 
                        id = "nombre" 
                        nombre = "nombre" 
                        placeholder="Ingrese su nombre:  " 
                        onChange={handleInputChange} 
                        defaultValue = {nombre}
                        ></input>


                    <label htmlFor = "correo">Correo</label>
                    <input 
                        type="text" 
                        id = "correo" 
                        nombre = "correo" 
                        placeholder="Ingrese su correo:  " 
                        onChange={handleInputChange} 
                        defaultValue = {correo}
                        ></input>

                    <label htmlFor = "contacto">Contacto</label>
                    <input 
                        type="text" 
                        id = "contacto" 
                        nombre = "contacto" 
                        placeholder="Ingrese su número de contacto:  " 
                        onChange={handleInputChange} 
                        defaultValue = {contacto}
                        ></input>   
                <input type="submit" value={id ? "update": "añadir"}></input>        
            </form>
        </div>

        );
    
};

export default Editor;
